export default {
	props:{
		// 
		list:{
			type:Array,
			default: function () {
				return [];
			}
		},
		// &
		vm: {
			type:Object,
			default: function () {
				return {}
			}
		},
		// 
		// form: {},
		form: {
			type:Object,
			default: function () {
				return {}
			}
		},
		// 
		obj: {
			type:Object,
			default: function () {
				return {}
			}
		},

	},
	data: function data() {
		var opens = $.db.get('opens') || [0];
		// var opens_str = ;
		// if(opens_str){
		// 	opens = opens_str;
		// }
		return {
			// 
			title: "",

			// 
			url: "",

			// 
			url_add: "",

			// 
			url_del: "",

			// 
			url_set: "",

			// 
			url_get_obj: "",

			// 
			url_get_list: "",

			// 
			url_submit: "",

			// 
			url_upload: "",

			// 
			url_import: "",

			// 
			url_export: "",

			// 
			config: {
				// 
				page: 1,
				// 
				size: 10
			},

			// 
			loading: 0,

			// 
			showing: 0,

			// 
			posting: 0,

			// 
			select: $.db.get('select'),

			// 
			count: 0,

			// truefalse
			show: false,

			// 
			bl: false,

			// 
			display: "",

			// 
			field: "",

			// 
			tip: "",

			// 
			mode: "list",

			// 
			clear_list: true,

			// 
			message: "",

			// 
			selects: "",

			// , 
			page_now: 1,

			// 
			select_state: false,

			// 
			sort_key: "display",

			// 
			query_set: {},

			// id
			opens: opens,

			// ID: father_id
			father_id: "father_id",

			// 
			selection: [],

			// 
			oauth: {
				"signIn": true,
				"gm": 0,
				"user_admin": []
			},

			// 
			user: this.$store.state.user,

			// 
			user_group: this.$store.state.user.user_group,

			// 
			tip_show: true,

		};
	},
	methods: {

		/**
		 * @description , 
		 * @param {String} name 
		 * @param {Object} param1 1
		 * @param {Object} param2 2
		 * @param {Object} param3 3
		 * @return {Object} 
		 */
		events: function events(name, param1, param2, param3) {
			if (this[name]) {
				if (param3) {
					return this[name](param1, param2, param3);
				} else {
					return this[name](param1, param2);
				}
			} else {
				return null;
			}
		},

		/**
		 * @description 
		 * @param {Object} param 
		 * @param {Function} func 
		 */
		add: function add(param, func) {
			if (!param) {
				param = this.obj;
			}
			var pm = this.events("add_before", Object.assign({}, param)) || param;
			var msg = this.events("add_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("add_main", pm, func);
			}
			return ret;
		},

		/**
		 * @description 
		 * @param {Object} param 
		 */
		del: function del(param, func) {
			if (!param) {
				param = this.query;
			}
			var pm = this.events("del_before", Object.assign({}, param)) || param;
			var msg = this.events("del_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("del_main", pm, func);
			}
			return ret;
		},

		del_show: function(o, id) {
			var _this = this;
			$.confirm('!<br/>?', function() {
				// console.log('!');
				var query = {};
				query[id] = o[id];
				_this.del(query, function() {
					_this.list.del(query);
					_this.count -= 1;
				});
			}, function() {
				// console.log('!')
			})
		},

		/**
		 * @description 
		 * @param {Object} param 
		 * @param {String} query 
		 * @param {Boolean} includeZero 0
		 */
		set: function set(param, query, func, includeZero) {
			if (!param) {
				param = this.obj;
			}
			if (query) {
				this.query_set = query;
			} else {
				this.query_set = Object.assign({}, this.query);
			}
			var pm = this.events("set_before", Object.assign({}, param), includeZero) || param;
			var msg = this.events("set_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("set_main", pm, func);
			}
			return ret;
		},

		/**
		 * 
		 * @param {Object} param
		 * @param {Boolean} includeZero 0
		 * @param {Object} 
		 */
		set_before: function set_before(param, includeZero) {
			var pm = $.delete(param, includeZero);
			for (var k in pm) {
				if (k.toLocaleLowerCase().indexOf('time') !== -1 && pm[k].indexOf('T') !== -1) {
					pm[k] = new Date(pm[k]).toStr('yyyy-MM-dd 00:00:00');
				}
			}
			return pm;
		},

		/**
		 * 
		 */
		batchSet: function batchSet() {
			var _this = this;
			$.confirm('<br/>?', function() {
				var q = Object.assign({}, _this.query, _this.query_set);
				q[_this.field] = _this.selects;
				delete q.page;
				delete q.size;
				delete q.orderby;
				_this.set(_this.form, q, function(json) {
					if (json.result) {
						_this.show = false;
						_this.get();
					}
				}, true);
			});
		},

		/**
		 * @description 
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		get_list: function get_list(param, func) {
			if (!param) {
				param = this.query;
			}
			var pm = this.events("get_list_before", Object.assign({}, param)) || param;
			var msg = this.events("get_list_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("get_list_main", pm, func);
			}
			return ret;
		},

		/**
		 * @description 
		 * @param {Object} query 
		 * @func {Function} 
		 */
		get_obj: function get_obj(param, func) {
			console.log("", param)
			if (!param) {
				param = this.query;
			}
			var pm = this.events("get_obj_before", Object.assign({}, param)) || param;
			var msg = this.events("get_obj_check", pm);
			var ret;
			if (!msg) {
				// console.log("", param)
				ret = this.events("get_obj_main", pm, func);
			} else if (func) {
				func();
			}
			return ret;
		},

		/**
		 * 
		 * @param {Object} param
		 * @param {Object} func
		 */
		sort: function sort(param, func) {
			var pm = this.events("sort_before", Object.assign({}, param)) || param;
			var msg = this.events("sort_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("sort_main", pm, func);
			}
			return ret;
		},

		/**
		 * 
		 * @param {Function} func 
		 */
		check_auth: function check_auth(func) {
			if (this.oauth.signIn) {
				if (this.user.user_id) {
					func();
				} else {
					this.$get_user(func);
				}
			} else {
				func();
			}
		},

		/**
		 * 
		 * @param {Object} param 
		 * @param {Function} func 
		 */
		init: function init(param, func) {
			var pm = this.events("init_before", Object.assign({}, param)) || param;
			var msg = this.events("init_check", pm);
			var ret;
			if (!msg) {
				ret = this.events("init_main", pm, func);
			} else if (func) {
				func();
			}
			return ret;
		},

		/**
		 * 
		 * @param {Object} param 
		 * @param {Function} func 
		 */
		upload: function upload(param, func) {
			var pm = this.events("upload_before", Object.assign({}, param)) || param;
			var msg = this.events("upload_check", pm);
			var ret;
			if (msg) {
				this.$toast(msg, 'danger');
			} else {
				ret = this.events("upload_main", pm, func);
			}
			return ret;
		},

		/**
		 * @description 
		 * @param {Object} value 
		 */
		add_main: function add_main(value, func) {
			var url = this.url ? this.url + "method=add" : this.url_add;
			if (!url) {
				return;
			}
			var _this = this;
			this.$post(url, value, function(json) {
				_this.events("add_after", json, func);
				if (json.result) {
					_this.$toast(json.result.tip, json.result.bl ? 'success' : 'danger');
				} else if (json.error) {
					_this.$toast(json.error.message, 'danger');
				} else {
					_this.$toast('! :!', "danger");
				}
			});
		},

		/**
		 * @description 
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		del_main: function del_main(query, func) {
			var url = this.url ? this.url + "method=del" : this.url_del;
			if (!url) {
				return;
			}
			var _this = this;
			this.$get(url, query, function(json) {
				_this.events("del_after", json, func);
				if (json.result) {
					_this.$toast(json.result.tip, json.result.bl ? 'success' : 'danger');
				} else if (json.error) {
					_this.$toast(json.error.message, 'danger');
				} else {
					_this.$toast('! :!', "danger");
				}
			});
		},

		/**
		 * 
		 * @param {Object} json 
		 * @param {Object} func 
		 */
		del_after: function del_after(json, func) {
			if (func) {
				func();
			}
		},

		/**
		 * @description 
		 * @param {Object} value 
		 * @param {Object} value 
		 */
		set_main: function set_main(value, func) {
			var url = this.url ? this.url + "method=set" : this.url_set;
			if (!url) {
				return;
			}
			var _this = this;
			if (!this.query_set || Object.keys(this.query_set).length === 0) {
				this.query_set = this.query;
			}
			this.$post(this.toUrl(this.query_set, url), value, function(json, status) {
				_this.events("set_after", json, func);
				if (json.result) {
					if (json.result.bl) {
						if (_this.tip_show) {
							_this.$toast(json.result.tip.replace('', ''), "success");
						}
					} else {
						_this.$toast(json.result.tip.replace('', ''), "danger");
					}
				} else if (json.error) {
					_this.$toast(json.error.message, "danger");
				} else {
					_this.$toast('! :!', "danger");
				}
			});
		},

		/**
		 * 
		 * @param {Object} json 
		 * @param {Object} func 
		 */
		set_after: function set_after(json, func) {
			if (func) {
				func(json);
			}
		},

		/**
		 * @description 
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		get: function get(query, func) {
			this.get_main(query, func);
		},

		/**
		 * @description ()
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		get_main: function get_main(query, func) {
			// console.log("get_obj_main");
			var url = this.url_get_obj ? this.url_get_obj : this.url;
			if (url) {
				var _this = this;
				this.get_obj(query, function() {
					_this.get_create(query, func);
				});
			} else {
				this.get_create(query, func);
			}
		},

		/**
		 * 
		 * @param {Object} param 
		 */
		get_obj_check: function get_obj_check(param) {
			var bl = false;
			for (var k in param) {
				if (param[k]) {
					bl = true;
					break;
				};
			}
			if (bl) {
				return null;
			} else {
				return "";
			}
		},

		/**
		 * @description ()
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		get_obj_main: function get_obj_main(query, func) {
			// console.log("get_obj_main");
			var url = this.url_get_obj ? this.url_get_obj : this.url + "method=get_obj";
			if (!url) {
				return;
			}
			var _this = this;
			this.$get(this.toUrl(query, url), null, function(json, status) {
				_this.events("get_obj_after", json, func);
				var res = json.result;
				if (res) {
					var obj;
					if (res.obj) {
						obj = res.obj;
						delete res.obj;
					} else {
						var list = res.list;
						if (list && list.length > 0) {
							obj = list[0];
						} else {
							obj = res;
						}
					}
					if (obj) {
						if (_this.obj || Object.keys(_this.obj).length === 0) {
							_this.obj = obj;
						} else {
							$.push(_this.obj, obj);
						}
						var o = _this.obj;
						for (var k in o) {
							if (k.indexOf('time') !== -1) {
								var val = o[k];
								if (val && val.indexOf('T') !== -1) {
									var v = new Date(o[k]);
									o[k] = v.toStr('yyyy-MM-dd hh:mm:ss');
								}
							}
						}
						if (_this.form || Object.keys(_this.form).length === 0) {
							_this.form = Object.assign({}, _this.obj)
						} else {
							$.push(_this.form, Object.assign({}, _this.obj));
						}
					}
				} else if (json.error) {
					console.log(json.error.message);
				} else {
					_this.$toast("", "danger");
				}
			});
		},

		/**
		 * @description 
		 * @param {Object} json 
		 */
		get_obj_after: function get_obj_after(json, func) {
			if (func) {
				func(json);
			}
		},

		/**
		 * @description ()
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		get_list_main: function get_list_main(query, func) {
			var url = this.url_get_list ? this.url_get_list : this.url;
			if (!url) {
				return;
			}
			var _this = this;
			this.loading = 0;
			this.$get(this.toUrl(query, url), null, function(json, status) {
				_this.loading = 100;
				if (_this.clear_list) {
					_this.list.clear();
				}
				_this.events("get_list_after", json, func, url);
				var res = json.result;
				if (res) {
					_this.page_now = _this.query.page;
					_this.list.addList(res.list);
					if (res.count !== undefined) {
						_this.count = res.count;
					}
				} else if (json.error) {
					console.log(json.error.message);
				} else {
					_this.$toast("", "danger");
				}
			});
		},

		/**
		 * @description 
		 * @param {Object} res 
		 */
		get_list_after: function get_list_after(res, func, url) {
			if (func) {
				func(res, url);
			}
		},

		/**
		 * 
		 * @param {Object} query 
		 * @param {Boolean} bl 
		 */
		search: function search(query, func) {
			if (query) {
				$.push(this.query, query);
			}
			var url = this.url_get_list ? this.url_get_list : this.url;
			if (url) {
				this.query.page = 1;
				this.count = 0;
				$.route.push("?" + this.toUrl(this.query));
				this.first(query, func);
			}
		},

		get_create: function get_create(query, func) {
			if (query) {
				$.push(this.query, query);
			}
			var url = this.url_get_list ? this.url_get_list : this.url;
			if (url) {
				this.count = 0;
				$.route.push("?" + this.toUrl(this.query));
				this.first(query, func);
			}
		},

		/**
		 * @description  ()
		 * @param {Object} query 
		 * @param {Function} func 
		 */
		first: function first(query, func) {
			var _this = this;
			if (!this.count) {
				var qy = Object.assign({}, this.query);
				this.get_list(qy, func);
			} else {
				this.get_list(query, func);
			}
		},

		/**
		 * @description 
		 * @param {Function} func 
		 */
		next: function next(query, func) {
			console.log("next");
			var _this = this;
			_this.get_list(query, function(json, url) {
				if (json.result) {
					var list = json.result.list;
					if (list.length > 0) {
						var qy = Object.assign({}, query, {
							page: query.page + 1
						});
						if (qy.page <= _this.page_count) {
							_this.$get(_this.toUrl(qy, url));
						}
					}
				}
				if (func) {
					func(json);
				}
			});
		},

		/**
		 * @description 
		 * @param {Function} func 
		 */
		prev: function prev(query, func) {
			console.log("prev");
			var _this = this;
			this.get_list(query, function(json, url) {
				if (json.result) {
					var list = json.result.list;
					if (list.length > 0) {
						var qy = Object.assign({}, query, {
							page: query.page - 1
						});
						if (qy.page >= 1) {
							_this.$get(_this.toUrl(qy, url));
						}
					}
				}
				if (func) {
					func(res);
				}
			});
		},

		/**
		 * 
		 * @param {Object} query
		 */
		clear: function clear(query) {
			$.clear(query);
		},

		/**
		 * 
		 */
		reset: function reset() {
			$.clear(this.query);
			$.push(this.query, this.config);
			this.get_list();
		},

		/**
		 * 
		 */
		submitForm() {
			this.$refs["form"].validate((valid) => {
				if (valid) {
					this.submit();
				} else {
					console.error('error !!');
				}
			});
		},

		/**
		 * 
		 * @param {Object} param 
		 * @param {Object} func 
		 */
		submit: function submit(param, func) {
			console.log('');
			if (!param) {
				param = this.form;
			}
			var pm = this.events("submit_before", Object.assign({}, param)) || param;
			var msg = this.events("submit_check", pm);
			var ret;
			if (msg) {
				this.$toast(msg, 'danger');
			} else {
				ret = this.events("submit_main", pm, func);
			}
			return ret;
		},

		/**
		 * 
		 * @param {Object} param 
		 */
		submit_before: function(param) {
			// console.log('', param);
			return param;
		},

		/**
		 * 
		 * @param {Object} 
		 * @return {String} null, 
		 */
		submit_check: function submit_check(param) {

			return null;
		},

		/**
		 * 
		 */
		submit_main: function submit_main(param, func) {
			console.log('', param);
			var url = this.url;
			console.log("",url);
			if (url) {
				if (this.field) {
					var id = param[this.field];
					if (id) {
						var q = {
							method: 'set'
						};
						q[this.field] = id;
						url = this.toUrl(q, url);
					} else {
						url += "method=add"
					}
				} else {
					url += "method=submit"
				}
			} else if (this.url_submit) {
				url = this.url_submit;
			} else if (this.field) {
				// console.log('', url);
				var id = param[this.field];
				if (id) {
					url = this.url_set;
				} else {
					url = this.url_add;
				}
			}

			console.log('', url);
			if (url) {
				var _this = this;
				// console.log('', url, param);
				url = this.$toUrl(this.query, url);
				this.$post(url, param, function(json, status) {
					if (json.result) {
						_this.$toast('', 'success');
						_this.events("submit_after", json, func);
					} else if (json.error) {
						_this.$toast(json.error.message, 'danger');
					} else {
						_this.$toast("", "danger");
					}
				});
			}
		},

		/**
		 * @description 
		 * @param {Object} json 
		 * @param {Function} func 
		 */
		submit_after: function submit_after(json, func) {
			if (func) {
				func(json);
			}
			this.$router.go(-1);
		},

		/**
		 * 
		 * @param {Number} n 
		 */
		go: function go(n) {
			var page = this.query.page + n;
			this.goTo(page);
		},

		/**
		 * 
		 * @param {Number} page 
		 */
		goTo: function goTo(page) {
			if (page < 1) {
				page = 1;
			} else if (page > this.page_count) {
				page = this.page_count;
			}
			var query = this.query;
			var p = query.page;
			query.page = page;
			this.$router.push("?" + this.toUrl(query));
			if (this.page_count !== 0) {
				if (p + 1 == page) {
					this.next(query);
				} else if (p - 1 == page) {
					this.prev(query);
				} else {
					this.first(query);
				}
			} else {
				this.first(query);
			}
		},

		/**
		 * @description 
		 * @param {Object} obj 
		 * @param {String} url 
		 * @return {String} url
		 */
		toUrl: function toUrl(obj, url) {
			return $.toUrl(obj, url);
		},

		/**
		 * 
		 */
		init_before: function init_before(query) {
			if (!query) {
				query = this.config;
			}
			return query;
		},

		/**
		 * 
		 */
		init_main: function init_main(query) {
			var _this = this;
			$.push(this.query, query);
			_this.init_after(function() {
				_this.get(_this.query);
			});
		},

		/**
		 * 
		 */
		init_after: function init_after(func) {
			if (func) {
				func();
			}
		},

		/**
		 * @description 
		 * @param {Function} func 
		 */
		upload_main: function upload_main(func) {
			var url = "";
			if (this.url) {
				url = this.url + "method=upload";
			} else {
				url = this.url_upload;
			}

			if (!param) {
				param = this.form;
			}
			if (msg) {
				this.$toast(msg, 'danger');
			} else {
				this.uploading = 0;
				var _this = this;
				this.$upload(url, param, function(json, status) {
					_this.uploading = 100;
					_this.events("upload_after", json, func);
				});
			}
		},

		/**
		 * @description 
		 * @param {Object} json 
		 * @param {Function} func
		 */
		upload_after: function upload_after(json, func) {
			if (json.result) {
				this.$toast(json.result.tip, json.result.bl ? 'success' : 'danger');
			} else if (json.error) {
				this.$toast(json.error.message, 'danger');
			} else {
				this.$toast("", "danger");
			}
			if (func) {
				func();
			}
		},

		/**
		 * 
		 * @param {Object} param 
		 */
		end_before: function end_before(param) {
			// this.reset();
		},

		/**
		 * 
		 */
		select_all: function select_all() {
			var bl = !this.select_state;
			if (!bl) {
				this.selects = '';
			} else {
				var s = '';
				var list = this.list;
				for (var i = 0; i < list.length; i++) {
					s += '|' + list[i][this.field];
				}
				this.selects = s.replace('|', '');
			}
			this.select_state = bl;
		},

		/**
		 * 
		 * @param {String|Number} id ID
		 */
		select_change: function select_change(id) {
			var has = false
			var arr = this.selects.split('|');
			for (var i = 0; i < arr.length; i++) {
				var o = arr[i];
				if (id == o) {
					arr.splice(i, 1);
					has = true;
					break;
				}
			}
			if (!has) {
				arr.push(id)
			}
			var s = arr.join('|');
			if (s.indexOf('|') == 0) {
				this.selects = s.substring(1)
			} else {
				this.selects = s;
			}
		},

		/**
		 * 
		 * @param {String|Number} id ID
		 */
		select_has: function select_has(id) {
			var ids = '|' + this.selects + '|';
			return ids.indexOf('|' + id + '|') !== -1;
		},

		/**
		 * 
		 * @param {Number} index 
		 */
		selected: function selected(index) {
			this.select = index;
			$.db.set('select', index, 120);
		},

		/**
		 * 
		 * @param {Object} e 
		 */
		page_change: function page_change(e) {
			var n = Number(e.target.value);
			if (isNaN(n)) {
				n = 1;
			}
			if (n < 1) {
				n = 1;
			} else if (n > this.page_count) {
				n = this.page_count
			}
			this.page_now = n;
		},

		/**
		 * 
		 * @param {Array} list 
		 * @param {String} arr_str id
		 * @param {String} key 
		 * @param {String} name 
		 * @param {String} span 
		 */
		get_name(list, arr_str, key, name, span) {
			if (!name) {
				name = "name";
			}
			var value = "";
			if (arr_str) {
				if (typeof(arr_str) == 'string') {
					if (!span) {
						span = ',';
					}
					var arr = arr_str.split(span);
					var id = Number(arr[0]);

					for (var i = 0; i < list.length; i++) {
						var o = list[i];
						if (o[key] == id) {
							value += '|' + o[name];
						}
					}
				} else {
					var id = arr_str;
					for (var i = 0; i < list.length; i++) {
						var o = list[i];
						if (o[key] == id) {
							value = o[name];
							break
						}
					}
				}
			}
			return value.replace('|', '');
		},

		/**
		 * 
		 */
		cancel: function cancel() {
			this.$router.go(-1);
		},

		/**
		 * 
		 * @param {Object} file 
		 */
		import_db: function import_db(file) {
			if (file) {
				var _this = this;
				$.confirm(" " + file.name, "", function() {
					$.http.upload(_this.url_import, file, function(json) {
						if (json.result) {
							$.confirm(json.result.tip, function() {
								_this.get();
							});
						} else if (json.error) {
							$.confirm(json.error.message);
						} else {
							_this.$toast("", "danger");
						}
					});
				});
			}
		},

		/**
		 * 
		 */
		export_db: function export_db() {
			var _this = this;
			if (this.selects) {
				var query = {};
				query[this.field] = this.selects;
				this.$get(_this.url_export, query, function(json) {
					var res = json.result;
					if (res && res.bl) {
						window.location.href = res.url;
					}
				});
			} else {
				this.$get(_this.url_export, this.query, function(json) {
					var res = json.result;
					if (res && res.bl) {
						window.location.href = res.url;
					}
				});
			}
		},

		/**
		 * 
		 * @param {Number} id ID
		 * @param {Object} list 
		 * @return {Number} 
		 */
		opens_has_sub: function(id, list) {
			if (!list) {
				list = this.list;
			}
			var bl = false;
			var father_id = this.father_id;
			for (var i = 0; i < list.length; i++) {
				var o = list[i];
				if (o[father_id] === id) {
					bl = true;
					break;
				}
			}
			return bl;
		},

		/**
		 * 
		 * @param {Number} id 
		 */
		opens_change: function opens_change(id) {
			var index = this.opens.indexOf(id);
			if (index !== -1) {
				this.opens.splice(index, 1);
			} else {
				var bl = this.opens_has_sub(id);
				if (bl) {
					this.opens.push(id);
				}
			}
			$.db.set('opens', this.opens);
		},

		/**
		 * 
		 * @param {Number} id 
		 * @return {Boolean} true, false
		 */
		opens_has: function opens_has(id) {
			return this.opens.indexOf(id) !== -1;
		},

		/**
		 * , 5
		 * @param {Number} fid ID
		 * @param {Object} list 
		 * @return {Number} 
		 */
		opens_lv: function opens_lv(fid, list) {
			if (!list) {
				list = this.list;
			}
			var lv = 0;
			var father_id = this.father_id;
			var id = this.field;
			var num = fid;
			for (var n = 0; n < 5; n++) {
				if (num === 0) {
					break;
				}
				for (var i = 0; i < list.length; i++) {
					var o = list[i];
					if (o[id] === num) {
						lv++;
						num = o[father_id];
						if (num === 0) {
							break;
						}
					}
				}
			}
			return lv;
		},

		/**
		 * 
		 * @param {Object} value
		 * @param {Object} row
		 */
		filterState(value, row) {
			return row.state === value;
		},

		// 
		delInfo() {
			var list = this.selection;
			if (list.length === 0) {
				this.$message({
					type: 'info',
					message: '!'
				});
				return;
			}
			this.$confirm(', ?', '', {
				confirmButtonText: '',
				cancelButtonText: '',
				type: 'warning'
			}).then(async () => {
				console.log("");
				await this.delAll(list);
			}).catch(() => {
				this.$message({
					type: 'info',
					message: ''
				});
			});
		},

		/**
		 * 
		 * @param {Object} list 
		 */
		async delAll(list) {
			console.log("2")
			var bl = false;
			for (var i = 0; i < list.length; i++) {
				var o = await this.$get("~/api/user/del?", {
					user_id: list[i].user_id
				});
				if (o.error) {
					this.$message.error('' + o.error.message);
					break;
				} else {
					bl = true;
				}
			}
			if (bl) {
				this.$message({
					type: 'success',
					message: '!'
				});
				this.get_list()
			}
		},

		/**
		 * 
		 * @param {Object} file 
		 * @param {key} 
		 */
		uploadFile(file, key = "img") {
			var _this = this;
			var form = new FormData() // FormData 
			form.append('file', file) // 
			this.$upload(this.url_upload, form, function(json) {
				if (json.result) {
					console.log(json.result)
					_this.form[key] = _this.$fullUrl("~" + json.result.url);
				} else {
					_this.$toast('');
				}
			});
		},

		/**
		 * 
		 * @param {Object} val
		 */
		selectionChange(val) {
			this.selection = val;
		},

		/**
		 * 
		 * @param {Object} size
		 */
		handleSizeChange(size) {
			this.query.size = size;
			this.get_list();
		},

		/**
		 * 
		 * @param {Object} page
		 */
		handleCurrentChange(page) {
			this.query.page = page;
			this.get_list();
		},

	},
	computed: {
		/**
		 * 
		 */
		page_count: function page_count() {
			return Math.ceil(this.count / this.query.size);
		}
	},
	created: function created() {
		this.showing = 0;
		this.init(this.$route.query);
		// this.check_auth(() => {
		// 	this.init(this.$route.query);
		// })
	},
	mounted: function mounted() {
		this.showing = 100;
	},
	beforeDestroy: function beforeDestroy() {
		this.events('end_before');
	}
};
